import Button from 'react-bootstrap/Button'
import Container from 'react-bootstrap/Container'
import {useState} from 'react'

const User = (props: {index:number, isActive: boolean, currentUser: string, name: string, logs:Array<string>, popup: (type: string, name?: string)=>void}) =>{

  const [open, setOpen] = useState(false)

  const deleteUser = () =>{
    props.popup('delete', props.name)
  }

  return(
    <Container onClick={()=>{setOpen(!open)}} className={props.currentUser === props.name ? 'userContainer you' : 'userContainer'}>
    <p>{index}</p>
    <div className='userHeaderContainer'>
    <h3>{props.name}</h3>
    {props.isActive ?
      <Button onClick={deleteUser} className='deleteUserButton' variant='danger'>Delete</Button>
        :
      <p>Activate you account to delete users</p>
    }

    </div>
    {open ?
     <div>
    {props.logs.map((log) =>{return <p key={log}>{log}</p>})}
    </div>
    : null}

    </Container>
  )
}

export default User
