import React from 'react'
import Button from 'react-bootstrap/Button'
import LogInForm from './LogInForm'
import CreateAccountForm from './CreateAccountForm'
import RestoreAccountForm from './ResotreAccountForm'
import '../App.css'




class LogInPage extends React.Component<{}, {logIn: boolean, createAccount: boolean, restorePassword: boolean, message: string}>{

  constructor(props: any){
    super(props)
    this.state ={
      logIn: true,
      createAccount: false,
      restorePassword: false,
      message: ""
    }
    this.changeFormLogin = this.changeFormLogin.bind(this)
    this.changeFormCreate = this.changeFormCreate.bind(this)
    this.changeFormRestore = this.changeFormRestore.bind(this)
    this.sendLogin = this.sendLogin.bind(this)
  }

  sendLogin = async (data: {email: string, passowrd: string}) =>{
    console.log(JSON.stringify(data))
    try{
      const response = await fetch('http://localhost:4000/login',
       {method: 'POST',
        mode: 'cors',
        headers:{'Content-Type' : 'application/json'},
        body: JSON.stringify(data)})
        .then(response => {
          if(response.ok){
            response.json().then(json => {
              console.log(json)
              if(json.status === true){
                console.log('set off function to chage page')
              }else{
                this.setState({message: json.message})
              }
            })
          }else if(!response.ok){
            throw Error(response.statusText)
          }
        })

    } catch (error){
      console.log(error)
    }
  }

  sendCreateAccount(){
    console.log('send create account data')
  }

  sendResetPassword(){
    console.log('send reset password data')
  }

  changeFormCreate(){
    this.setState({
      logIn: false,
      createAccount: true,
      restorePassword: false
    })
  }

  changeFormRestore(){
    this.setState({
      logIn: false,
      createAccount: false,
      restorePassword: true
    })
  }

  changeFormLogin(){
    this.setState({
      logIn: true,
      createAccount: false,
      restorePassword: false
    })
  }

  render(){
  return(
    <div id='loginContainer'>
    {this.state.logIn ?
       <div>
        <LogInForm sendLogin={this.sendLogin}/>
        <div className='buttonContainer'>
        <Button onClick={this.changeFormCreate} variant='link'>Create an account</Button>
        <Button onClick={this.changeFormRestore} variant='link'>Forgot the password? - Restore password</Button>
        </div>
       </div>
    : null}

    {this.state.createAccount ?
      <div>
        <CreateAccountForm/>
        <div className='buttonContainer'>
        <Button onClick={this.changeFormLogin} variant='link'>Already have an account? - Log in</Button>
        <Button onClick={this.changeFormRestore} variant='link'>Forgot the password? - Restore password</Button>
        </div>
      </div>
    :null}

    {this.state.restorePassword ?
      <div>
        <RestoreAccountForm/>
        <div className='buttonContainer'>
        <Button onClick={this.changeFormCreate} variant='link'>Create an account</Button>
        <Button onClick={this.changeFormLogin} variant='link'>Already have an account? - Log in</Button>
        </div>
      </div>
    :null}
    {this.state.message ? <div>{this.state.message}</div> : null}
    </div>
  )
  }
}

export default LogInPage
