import React from 'react'
import Button from 'react-bootstrap/Button'
import LogInForm from './LogInForm'
import CreateAccountForm from './CreateAccountForm'
import RestoreAccountForm from './ResotreAccountForm'
import '../App.css'
import Alert from 'react-bootstrap/Alert'




class LogInPage extends React.Component<{login: () => void}, {logIn: boolean, createAccount: boolean, restorePassword: boolean, showMessage: boolean, message: {head: string, body: string}}>{

  constructor(props: any){
    super(props)
    this.state ={
      logIn: true,
      createAccount: false,
      restorePassword: false,
      showMessage: false,
      message: {head: '', body: ''}
    }
    this.changeFormLogin = this.changeFormLogin.bind(this)
    this.changeFormCreate = this.changeFormCreate.bind(this)
    this.changeFormRestore = this.changeFormRestore.bind(this)
    this.sendLogin = this.sendLogin.bind(this)
    this.sendCreateAccount = this.sendCreateAccount.bind(this)
  }


  sendLogin = async (data: {email: string, passowrd: string}) =>{
    try{
      const response = await fetch('http://localhost:4000/login',
       {method: 'POST',
        mode: 'cors',
        headers:{'Content-Type' : 'application/json'},
        body: JSON.stringify(data)})
        .then(response => {
          if(response.ok){
            response.json().then(json => {
              console.log(json)
              if(json.status === true){
                this.props.login()
              }else{
                this.setState({
                  showMessage: true,
                  message: {head: json.message.head, body: json.message.body}
                })
              }
            })
          }else if(!response.ok){
            throw Error(response.statusText)
          }
        })

    } catch (error){
      console.log(error)
    }
  }

  sendCreateAccount(data: {email: string, password: string, repeatPassword: string}){
    if(data.password === data.repeatPassword){
      console.log('create account')
    }else{
      this.setState({
        showMessage: true,
        message: {head: 'Passwords dont match', body: 'Check that both password fields are the same.'}
      })
    }
  }

  sendResetPassword(){
    console.log('send reset password data')
  }

  changeFormCreate(){
    this.setState({
      logIn: false,
      createAccount: true,
      restorePassword: false,
      showMessage: false
    })
  }

  changeFormRestore(){
    this.setState({
      logIn: false,
      createAccount: false,
      restorePassword: true,
      showMessage: false,
    })
  }

  changeFormLogin(){
    this.setState({
      logIn: true,
      createAccount: false,
      restorePassword: false,
      showMessage: false,
    })
  }

  render(){
  return(
    <div id='loginContainer'>
    {this.state.logIn ?
       <div>
        <LogInForm sendLogin={this.sendLogin}/>
        <div className='buttonContainer'>
        <Button onClick={this.changeFormCreate} variant='link'>Create an account</Button>
        <Button onClick={this.changeFormRestore} variant='link'>Forgot the password? - Restore password</Button>
        </div>
       </div>
    : null}

    {this.state.createAccount ?
      <div>
        <CreateAccountForm sendCreateAccount={this.sendCreateAccount}/>
        <div className='buttonContainer'>
        <Button onClick={this.changeFormLogin} variant='link'>Already have an account? - Log in</Button>
        <Button onClick={this.changeFormRestore} variant='link'>Forgot the password? - Restore password</Button>
        </div>
      </div>
    :null}

    {this.state.restorePassword ?
      <div>
        <RestoreAccountForm/>
        <div className='buttonContainer'>
        <Button onClick={this.changeFormCreate} variant='link'>Create an account</Button>
        <Button onClick={this.changeFormLogin} variant='link'>Already have an account? - Log in</Button>
        </div>
      </div>
    :null}
    {this.state.showMessage ?
       <Alert variant='danger' className='loginAlert'>
       <Alert.Heading>{this.state.message.head}</Alert.Heading>
       <p>{this.state.message.body}</p>
       </Alert>
        : null}
    </div>
  )
  }
}

export default LogInPage
