import React from 'react'
import LogInPage from './LogInPage'
import InterfacePage from './InterfacePage'

class Main extends React.Component<{}, {isLoading: boolean, sortedUsers: Array<Array<{name:string, logs:Array<string>}>>, unsortedUsers: Array<{name:string, logs:Array<string>}>, isLoggedIn: boolean, addingUser: boolean, user:string, showMessage: boolean, message: {head: string, body: string}, openPopup: boolean}>{

  constructor(props: any){
    super(props)
    this.state = {
      isLoggedIn: false,
      user: '',
      showMessage: false,
      message: {body: '', head: ''},
      openPopup: false,
      addingUser: false,
      unsortedUsers: [],
      sortedUsers: [],
      isLoading: false,

    }
    this.sendData = this.sendData.bind(this)
    this.sendLogin = this.sendLogin.bind(this)
    this.sendCreateAccount = this.sendCreateAccount.bind(this)
    this.sendResetPassword = this.sendResetPassword.bind(this)
  }

  sendData = async (data:any, url:string, method: string) => {
          const response =  await fetch(`http://localhost:4000/${url}`,
          {
            method: method,
            mode: 'cors',
            headers: this.requestInterceptor(),
            credentials: 'include',
            body: JSON.stringify(data)
          })
          .then(async response => {

            if(!response.ok){
              console.log(Error(response.statusText))
            }
                return response.json().then(json =>{
                return json
              })
          })
          return response


  }

  requestInterceptor = () =>{
    let token = localStorage.getItem('token')
    if(!token){
      return new Headers({
        'Content-Type' : 'application/json'
      })
    }
    return new Headers({
      'Authorization': `Bearer ${token}`,
      'Content-Type' : 'application/json'
    })
  }

  sendLogin = async (data: {email: string, password: string}) =>{
    const response = await this.sendData(data, 'login', 'POST')
    if(response.status){
      await this.getAllUsers(response.accessToken)
      localStorage.setItem('token', response.accessToken)
      localStorage.setItem('refreshToken', response.refreshToken)
      this.setState({isLoggedIn: true, user: response.data.email.toString(), showMessage: false, message: {head: '', body: ''}})
    }else if(!response.status){
      this.setState({
        showMessage: true,
        message: {head: response.data.head, body: response.data.body}
      })
    }
  }

  sendCreateAccount = async (data: {email: string, password: string, repeatPassword: string}) =>{
    if(data.password === data.repeatPassword){
        const response = await this.sendData(data, 'registration', 'POST')
        if(response.status){
          if(this.state.openPopup){
            this.setState({
              openPopup: false,
              unsortedUsers: [...this.state.unsortedUsers, {name: data.email, logs:[]}],
              sortedUsers: this.sortUsers([...this.state.unsortedUsers, {name: data.email, logs: data.logs}])
            })
          }
          this.setState({
            showMessage: true,
            message: {head: response.data.head, body:response.data.body}
          })
        }else if(!response.status){
          this.setState({
            showMessage: true,
            message: {head: response.data.head, body:response.data.body}
          })
        }
    }else{
      this.setState({
        showMessage: true,
        message: {head: 'Passwords dont match', body: 'Check that both password fields are the same.'}
      })
    }
  }

  deleteUser = async (name: string) =>{
    await this.sendData({"email":name}, 'delete', 'DELETE')
    .then(response => {if(!response.status){
      alert('Could not delete user, please try again')
    }else{
      this.popup('delete')
      let newUsers = this.state.unsortedUsers
      let userIndex = newUsers.indexOf(name)
      newUsers.splice(userIndex, 1)
      this.setState({
        unsortedUsers: newUsers,
        sortedUsers: this.sortUsers(newUsers)
      })

    }})
  }

  getAllUsers = async(token:string) =>{
    try{
    await fetch(`http://localhost:4000/users`,
    {
      method: 'GET',
      mode: 'cors',
      headers: new Headers({
        'Authorization': `Bearer ${token}`,
        'Content-Type' : 'application/json'
      }),
    })
    .then(response => {
      if(response.ok){
          return response.json()
          .then(json =>{
            console.log(json)
          this.setState({
            unsortedUsers: json,
            sortedUsers: this.sortUsers(json)
          })
        })
      }else if(!response.ok){
        throw Error(response.statusText)
      }
    })
  }catch (error){
    console.log(error)
  }
  }

  logOut = async () =>{
    let refreshToken = localStorage.getItem('refreshToken')
    await this.sendData({refreshToken}, 'logout', 'POST')
    localStorage.removeItem('token')
    localStorage.removeItem('refreshToken')
    this.setState({isLoggedIn: false})
  }


  sendResetPassword = async(data: {email: string}) =>{
    const response = await this.sendData(data, 'restore', 'POST')
    this.setState({
      showMessage: true,
      message: {head: response.data.head, body:response.data.body}
    })
  }

  hideMessage = () =>{
    this.setState({
      showMessage: false
    })
  }

  sortUsers = (data: Array<{name:string, logs:Array<string>}>) =>{
    let newUsers: Array<Array<{name:string, logs:Array<string>}>> = []
    for(let i =0; i<data.length; i++){
      let sliceableArray = data.slice(i*6, 6*(i+1))
      if(sliceableArray.length > 0)
        newUsers.push(sliceableArray)
      }
    return newUsers
  }

  popup = (type: string) =>{
    if(type === 'add'){
      this.setState({
        openPopup: !this.state.openPopup,
        addingUser: true
      })
    }else{
      this.setState({
        openPopup: !this.state.openPopup,
        addingUser: false
      })
    }
  }

  async componentDidMount(){
    console.log('component mounted')
    let refreshToken = localStorage.getItem('refreshToken')
    let token = localStorage.getItem('token')
    if(refreshToken){
      this.setState({isLoading: true})
      try{
      await fetch(`http://localhost:4000/refresh`,
      {
        method: 'POST',
        mode: 'cors',
        headers: new Headers({
          'Authorization': `Bearer ${token}`,
          'Content-Type' : 'application/json'
        }),
        body: JSON.stringify({refreshToken})
      })
      .then(response => {
        if(response.ok){
            return response.json()
            .then( async json =>{
              console.log(json)
              await this.getAllUsers(json.newTokens.accessToken)
              localStorage.setItem('token', json.newTokens.accessToken)
              localStorage.setItem('refreshToken', json.newTokens.refreshToken)
              this.setState({isLoggedIn: true, user: json.user.email.toString(), showMessage: false, message: {head: '', body: ''}})
          })
        }else if(!response.ok){
          throw Error(response.statusText)
        }
      })
    }catch (error){
      console.log(error)
    }
    finally{
      this.setState({isLoading:false})
    }
    }
  }


  render(){
    return (
      <div className="App">
      {this.state.isLoading ? <p>Loading...</p> :
        <div>
        {this.state.isLoggedIn ? <InterfacePage logOut={this.logOut} users={this.state.sortedUsers} deleteUser={this.deleteUser} addingUser={this.state.addingUser} popup={this.popup} openPopup={this.state.openPopup} showMessage={this.state.showMessage} sendCreateAccount={this.sendCreateAccount} message={this.state.message} name={this.state.user}/> : <LogInPage hideMessage={this.hideMessage} showMessage={this.state.showMessage} message={this.state.message} sendLogin={this.sendLogin} sendCreateAccount={this.sendCreateAccount} sendResetPassword={this.sendResetPassword}/>}
        </div>
         }
      </div>
    );
  }
}

export default Main
