import React from 'react'
import Button from 'react-bootstrap/Button'
import LogInForm from './LogInForm'
import CreateAccountForm from './CreateAccountForm'
import RestoreAccountForm from './ResotreAccountForm'
import '../App.css'
import Alert from 'react-bootstrap/Alert'




class LogInPage extends React.Component<{login: () => void}, {logIn: boolean, createAccount: boolean, restorePassword: boolean, showMessage: boolean, message: {head: string, body: string}}>{


private buttonCreate = React.createRef<HTMLButtonElement & Button>();
private buttonPassword = React.createRef<HTMLButtonElement & Button>();
private buttonLogin = React.createRef<HTMLButtonElement & Button>();

  constructor(props: any){
    super(props)
    this.state ={
      logIn: true,
      createAccount: false,
      restorePassword: false,
      showMessage: false,
      message: {head: '', body: ''}
    }
    this.changeFormLogin = this.changeFormLogin.bind(this)
    this.changeFormCreate = this.changeFormCreate.bind(this)
    this.changeFormRestore = this.changeFormRestore.bind(this)
    this.sendLogin = this.sendLogin.bind(this)
    this.sendCreateAccount = this.sendCreateAccount.bind(this)

    this.changeForm = this.changeForm.bind(this)

  }

  sendData = async (data:any, url:string) => {
          const response =  await fetch(`http://localhost:4000/${url}`,
          {
            method: 'POST',
            mode: 'cors',
            headers:{'Content-Type' : 'application/json'},
            body: JSON.stringify(data)
          })
          .then(response => {
            if(response.ok){
                return response.json().then(json =>{
                return json
              })
            }else if(!response.ok){
              throw Error(response.statusText)
            }
          })
          return response


  }

  sendLogin = async (data: {email: string, password: string}) =>{
    const test = await this.sendData(data, 'login')
    console.log(test)
    if(test.status){
      this.props.login()
    }else{
      this.setState({
        showMessage: true,
        message: {head: test.message.head, body: test.message.body}
      })
    }
  }

  sendCreateAccount = async (data: {email: string, password: string, repeatPassword: string}) =>{
    if(data.password === data.repeatPassword){

    }else{
      this.setState({
        showMessage: true,
        message: {head: 'Passwords dont match', body: 'Check that both password fields are the same.'}
      })
    }
  }


  sendResetPassword(){
    console.log('send reset password data')
  }

  changeFormCreate(){
    this.setState({
      logIn: false,
      createAccount: true,
      restorePassword: false,
      showMessage: false
    })
  }

  changeFormRestore(){
    this.setState({
      logIn: false,
      createAccount: false,
      restorePassword: true,
      showMessage: false,
    })
  }

  changeFormLogin(){
    this.setState({
      logIn: true,
      createAccount: false,
      restorePassword: false,
      showMessage: false,
    })
  }
changeForm = (e:any) =>{
  if(e.target === this.buttonCreate.current){
    this.setState({
      createAccount: true,
    })
  }else if(e.target === this.buttonPassword.current){
    this.setState({
      restorePassword: true,
    })
  }else if(e.target === this.buttonLogin.current){
    this.setState({
      logIn: true,
    })
  }
}
  render(){
  return(
    <div id='loginContainer'>
    {this.state.logIn ?
       <div>
        <LogInForm sendLogin={this.sendLogin}/>
        <div className='buttonContainer'>
        <Button onClick={this.changeForm} variant='link' ref={this.buttonCreate}>Create an account</Button>
        <Button onClick={this.changeForm} variant='link' ref={this.buttonPassword}>Forgot the password? - Restore password</Button>
        </div>
       </div>
    : null}

    {this.state.createAccount ?
      <div>
        <CreateAccountForm sendCreateAccount={this.sendCreateAccount}/>
        <div className='buttonContainer'>
        <Button onClick={this.changeForm} variant='link' ref={this.buttonLogin}>Already have an account? - Log in</Button>
        <Button onClick={this.changeForm} variant='link' ref={this.buttonPassword}>Forgot the password? - Restore password</Button>
        </div>
      </div>
    :null}

    {this.state.restorePassword ?
      <div>
        <RestoreAccountForm/>
        <div className='buttonContainer'>
        <Button onClick={this.changeForm} variant='link' ref={this.buttonCreate}>Create an account</Button>
        <Button onClick={this.changeForm} variant='link' ref={this.buttonLogin}>Already have an account? - Log in</Button>
        </div>
      </div>
    :null}
    {this.state.showMessage ?
       <Alert variant='danger' className='loginAlert'>
       <Alert.Heading>{this.state.message.head}</Alert.Heading>
       <p>{this.state.message.body}</p>
       </Alert>
        : null}
    </div>
  )
  }
}

export default LogInPage
