import React from 'react'
import Button from 'react-bootstrap/Button'
import LogInForm from './LogInForm'
import CreateAccountForm from './CreateAccountForm'
import RestoreAccountForm from './ResotreAccountForm'

class LogInPage extends React.Component<{}, {logIn: boolean, createAccount: boolean, restorePassword: boolean}>{

  constructor(props: any){
    super(props)
    this.state ={
      logIn: false,
      createAccount: false,
      restorePassword: true
    }
  }

  changeFormCreate(){
    this.setState({
      logIn: false,
      createAccount: true,
      restorePassword: false
    })
  }

  changeFormRestore(){
    this.setState({
      logIn: false,
      createAccount: false,
      restorePassword: true
    })
  }

  changeFormLogin(){
    this.setState({
      logIn: true,
      createAccount: false,
      restorePassword: false
    })
  }

  render(){
  return(
    <div>
    <div>
    {this.state.logIn ?
       <div>
        <LogInForm/>
        <Button onClick={this.changeFormCreate} variant='link'>Create an account</Button>
        <Button onClick={this.changeFormRestore} variant='link'>Forgot the password? - Restore password</Button>
       </div>
    : null}

    {this.state.createAccount ?
      <div>
        <CreateAccountForm/>
        <Button onClick={this.changeFormLogin} variant='link'>Already have an account? - Log in</Button>
        <Button onClick={this.changeFormRestore} variant='link'>Forgot the password? - Restore password</Button>
      </div>
    :null}

    {this.state.restorePassword ?
      <div>
        <RestoreAccountForm/>
        <Button onClick={this.changeFormCreate} variant='link'>Create an account</Button>
        <Button onClick={this.changeFormLogin} variant='link'>Already have an account? - Log in</Button>
      </div>
    :null}
    </div>
    </div>
  )
  }
}

export default LogInPage
