import React from 'react'
import Button from 'react-bootstrap/Button'
import LogInForm from './LogInForm'
import CreateAccoutForm from './CreateAccountForm'

class LogInPage extends React.Component<{}, {logIn: boolean, createAccount: boolean, restorePassword: boolean}>{

  constructor(props: any){
    super(props)
    this.state ={
      logIn: true,
      createAccount: false,
      restorePassword: false
    }
  }

  render(){
  return(
    <div>
    <div>
    {this.state.logIn ?
       <div>
        <LogInForm/>
        <Button variant='link'>Create an account</Button>
        <Button variant='link'>Forgot the password? - Restore password</Button>
       </div>
    : null}

    {this.state.createAccount ?
      <div>
        <CreateAccountForm/>
        <Button variant='link'>Already have an account? - Log in</Button>
        <Button variant='link'>Forgot the password? - Restore password</Button>
      </div>
    :null}

    <Button variant='link'>Already have an account? - Log in</Button>

    </div>
    </div>
  )
  }
}

export default LogInPage
