import React from 'react'
import Container from 'react-bootstrap/Container'
import User from './User'

class InterfacePage extends React.Component<{name: string},{users: Array<string>}> {

  constructor(props:any){
    super(props)
    this.state = {
      users: []
    }
  }

  async componentDidMount(){
    try{
    const response =  await fetch(`http://localhost:4000/allUsers`,
    {
      method: 'GET',
      mode: 'cors',
      headers:{'Content-Type' : 'application/json'},
    })
    .then(response => {
      if(response.ok){
          return response.json()
          .then(json =>{
          this.setState({
            users: json
          })
        })
      }else if(!response.ok){
        throw Error(response.statusText)
      }
    })
  }catch (error){
    console.log(error)
  }
  }

  render(){
    return(
      <div className='interfaceContainer'>
      <Container>
      <h1>{this.props.name}</h1>
      <div>
        ur name is {this.props.name}
        {this.state.users.map((user) => {return <User key={user} name={user}/>})}
      </div>
      </Container>
      </div>
    )
  }
}

export default InterfacePage
