import React from 'react'
import Container from 'react-bootstrap/Container'
import User from './User'
import Button from 'react-bootstrap/Button'
import Pupup from './Pupup'

class InterfacePage extends React.Component<{deleteUser: (name: string) => void, popup: (type: string)=>void, addingUser: boolean, name: string, openPopup: boolean, showMessage: boolean, message:{head: string, body:string}, sendCreateAccount: (data: {email: string, password: string, repeatPassword: string})=>void, users: Array<Array<string>>,},{userName: string, currentPage: number}> {

  constructor(props: {users: Array<Array<string>>, deleteUser: (name: string) => void, popup: (type: string)=>void, addingUser: boolean, deletingUser: boolean, name: string, openPopup: boolean, showMessage: boolean, message:{head: string, body:string}, sendCreateAccount: (data: {email: string, password: string, repeatPassword: string})=>void}){
    super(props)
    this.state = {
      userName: '',
      currentPage: 0
    }
    this.popup = this.popup.bind(this)
  }

  popup = (type:string, name?: string) =>{
    if(name !== undefined){
      this.setState({userName: name})
    }
    this.props.popup(type)
  }

  deleteUser = (name: string) =>{
    this.props.deleteUser(name)
  }

  render(){
    console.log(this.state)
    console.log(this.props)
    return(
      <div className='interfaceContainer'>
      {this.props.openPopup ? <Pupup deleteUser={this.deleteUser} userName={this.state.userName} addingUser={this.props.addingUser} showMessage={this.props.showMessage} sendCreateAccount={this.props.sendCreateAccount} message={this.props.message} closePopup={this.popup}/> : null}
      <Container>
      <div className='titleContainer'>
      <h1>{this.props.name}</h1>
      <Button onClick={(()=>{this.popup('add')})}>Add user</Button>
      </div>
      <div className='usersListContainer'>
      {this.props.users[0].map((user) => {return user === this.props.name ? null : <User popup={this.popup} key={user} name={user}/>})}
      </div>
      </Container>
      </div>
    )
  }
}

export default InterfacePage
