import React from 'react'
import Container from 'react-bootstrap/Container'
import User from './User'
import Button from 'react-bootstrap/Button'
import Pupup from './Pupup'

class InterfacePage extends React.Component<{name: string},{users: Array<string>, addingUser: boolean}> {

  constructor(props:any){
    super(props)
    this.state = {
      users: [],
      addingUser: false
    }
    this.addNewUser = this.addNewUser.bind(this)
  }

  popup = () =>{
    console.log('newUser')
    this.setState({
      addingUser: !this.state.addingUser
    })
  }

  async componentDidMount(){
    try{
    await fetch(`http://localhost:4000/allUsers`,
    {
      method: 'GET',
      mode: 'cors',
      headers:{'Content-Type' : 'application/json'},
    })
    .then(response => {
      if(response.ok){
          return response.json()
          .then(json =>{
          this.setState({
            users: json
          })
        })
      }else if(!response.ok){
        throw Error(response.statusText)
      }
    })
  }catch (error){
    console.log(error)
  }
  }

  render(){
    return(
      <div className='interfaceContainer'>
      {this.state.addingUser ? <Pupup closePopup={this.popup}/> : null}
      <Container>
      <div className='titleContainer'>
      <h1>{this.props.name}</h1>
      <Button onClick={this.popup}>Add user</Button>
      </div>
      <div className='usersListContainer'>
        {this.state.users.map((user) => {return <User key={user} name={user}/>})}
      </div>
      </Container>
      </div>
    )
  }
}

export default InterfacePage
