import React from 'react'
import Container from 'react-bootstrap/Container'
import User from './User'
import Button from 'react-bootstrap/Button'
import Pupup from './Pupup'
import UserPage from './UserPage'

class InterfacePage extends React.Component<{deleteUser: (name: string) => void, popup: (type: string)=>void, addingUser: boolean, name: string, openPopup: boolean, showMessage: boolean, message:{head: string, body:string}, sendCreateAccount: (data: {email: string, password: string, repeatPassword: string})=>void, users: Array<string>,},{userName: string, currentPage: number, sortedUsers: Array<Array<string>>}> {

  constructor(props: {users: Array<string>, deleteUser: (name: string) => void, popup: (type: string)=>void, addingUser: boolean, deletingUser: boolean, name: string, openPopup: boolean, showMessage: boolean, message:{head: string, body:string}, sendCreateAccount: (data: {email: string, password: string, repeatPassword: string})=>void}){
    super(props)
    this.state = {
      userName: '',
      currentPage: 0,
      sortedUsers: []
    }
    this.popup = this.popup.bind(this)
  }

  popup = (type:string, name?: string) =>{
    if(name !== undefined){
      //this.setState({userName: name})
    }
    this.props.popup(type)
  }

  deleteUser = (name: string) =>{
    this.props.deleteUser(name)
  }
/*
  sortUsers = () =>{
    let newUsers: Array<Array<string>> = []
    for(let i =0; i<this.props.users.length; i++){
      newUsers.push(this.props.users.splice(0, 6))
    }
    console.log(newUsers)
    return newUsers
  }
*/
  test = () =>{
    let newUsers: Array<Array<string>> = []
    for(let i =0; i<this.props.users.length; i++){
      console.log(this.state.userName)
      console.log(this.props.name)
      console.log(this.props.users[i])
      newUsers.push(this.props.users.splice(0, 6))
    }
    return <UserPage popup={this.popup} users={newUsers[this.state.currentPage]}/>
  }
//delete sortedUsers from state if u dont end up using it
//migth also remove username from state => there is a 'name' prop that provides it
//also change test to something elase if it works out
  render(){
    console.log(this.state)
    console.log(this.props)
    return(
      <div className='interfaceContainer'>
      {this.props.openPopup ? <Pupup deleteUser={this.deleteUser} userName={this.state.userName} addingUser={this.props.addingUser} showMessage={this.props.showMessage} sendCreateAccount={this.props.sendCreateAccount} message={this.props.message} closePopup={this.popup}/> : null}
      <Container>
      <div className='titleContainer'>
      <h1>{this.props.name}</h1>
      <Button onClick={(()=>{this.popup('add')})}>Add user</Button>
      </div>
      <div className='usersListContainer'>
      {this.test()}
      {/*this.props.users.map((user) => {return user === this.props.name ? null : <User popup={this.popup} key={user} name={user}/>})*/}
      </div>
      </Container>
      </div>
    )
  }
}

export default InterfacePage
