import React from 'react'
import LogInPage from './LogInPage'
import InterfacePage from './InterfacePage'

class Main extends React.Component<{}, {isLoggedIn: boolean, addingUser: boolean, user:string, showMessage: boolean, message: {head: string, body: string}, openPopup: boolean}>{

  constructor(props: any){
    super(props)
    this.state = {
      isLoggedIn: false,
      user: '',
      showMessage: false,
      message: {body: '', head: ''},
      openPopup: false,
      addingUser: false,

    }
    this.sendData = this.sendData.bind(this)
    this.sendLogin = this.sendLogin.bind(this)
    this.sendCreateAccount = this.sendCreateAccount.bind(this)
    this.sendResetPassword = this.sendResetPassword.bind(this)
  }

  sendData = async (data:any, url:string, method: string) => {
          const response =  await fetch(`http://localhost:4000/${url}`,
          {
            method: method,
            mode: 'cors',
            headers:{'Content-Type' : 'application/json'},
            body: JSON.stringify(data)
          })
          .then(response => {
            if(response.ok){
                return response.json().then(json =>{
                return json
              })
            }else if(!response.ok){
              console.log(Error(response.statusText))
            }
          })
          return response


  }

  sendLogin = async (data: {email: string, password: string}) =>{
    const response = await this.sendData(data, 'login', 'POST')
    if(response.status){
      this.setState({isLoggedIn: true, user: data.email.toString(), showMessage: false, message: {head: '', body: ''}})
    }else{
      this.setState({
        showMessage: true,
        message: {head: response.message.head, body: response.message.body}
      })
    }
  }

  sendCreateAccount = async (data: {email: string, password: string, repeatPassword: string}) =>{
    if(data.password === data.repeatPassword){
        const response = await this.sendData(data, 'create', 'POST')
        if(response.status){
          if(this.state.openPopup){
            this.setState({openPopup: false})
          }
          this.setState({
            showMessage: true,
            message: {head: response.message.head, body:response.message.body}
          })
        }else if(!response.status){
          this.setState({
            showMessage: true,
            message: {head: response.message.head, body:response.message.body}
          })
        }
    }else{
      this.setState({
        showMessage: true,
        message: {head: 'Passwords dont match', body: 'Check that both password fields are the same.'}
      })
    }
  }


  sendResetPassword = async(data: {email: string}) =>{
    const response = await this.sendData(data, 'restore', 'POST')
    this.setState({
      showMessage: true,
      message: {head: response.message.head, body:response.message.body}
    })
  }

  hideMessage = () =>{
    this.setState({
      showMessage: false
    })
  }

  popup = (type: string) =>{
    if(type === 'add'){
      this.setState({
        openPopup: !this.state.openPopup,
        addingUser: true
      })
    }else{
      this.setState({
        openPopup: !this.state.openPopup,
        addingUser: false
      })
    }
  }

  deleteUser = (name: string) =>{
    console.log(`delete user ${name}`)
    this.sendData(name, 'delete', 'DELETE')
  }





  render(){
    return (
      <div className="App">
        {this.state.isLoggedIn ? <InterfacePage deleteUser={this.deleteUser} addingUser={this.state.addingUser} popup={this.popup} openPopup={this.state.openPopup} showMessage={this.state.showMessage} sendCreateAccount={this.sendCreateAccount} message={this.state.message} name={this.state.user}/> : <LogInPage hideMessage={this.hideMessage} showMessage={this.state.showMessage} message={this.state.message} sendLogin={this.sendLogin} sendCreateAccount={this.sendCreateAccount} sendResetPassword={this.sendResetPassword}/>}
      </div>
    );
  }
}

export default Main
