import React from 'react'
import LogInPage from './LogInPage'
import InterfacePage from './InterfacePage'

class Main extends React.Component<{}, {sortedUsers: Array<Array<string>>, unsortedUsers: Array<string>, isLoggedIn: boolean, addingUser: boolean, user:string, showMessage: boolean, message: {head: string, body: string}, openPopup: boolean}>{

  constructor(props: any){
    super(props)
    this.state = {
      isLoggedIn: false,
      user: '',
      showMessage: false,
      message: {body: '', head: ''},
      openPopup: false,
      addingUser: false,
      unsortedUsers: [],
      sortedUsers: []

    }
    this.sendData = this.sendData.bind(this)
    this.sendLogin = this.sendLogin.bind(this)
    this.sendCreateAccount = this.sendCreateAccount.bind(this)
    this.sendResetPassword = this.sendResetPassword.bind(this)
  }

  sendData = async (data:any, url:string, method: string) => {
          const response =  await fetch(`http://localhost:4000/${url}`,
          {
            method: method,
            mode: 'cors',
            headers:{'Content-Type' : 'application/json'},
            body: JSON.stringify(data)
          })
          .then(response => {
            if(response.ok){
                return response.json().then(json =>{
                return json
              })
            }else if(!response.ok){
              console.log(Error(response.statusText))
            }
          })
          return response


  }

  sendLogin = async (data: {email: string, password: string}) =>{
    const response = await this.sendData(data, 'login', 'POST')
    if(response.status){
      this.setState({isLoggedIn: true, user: data.email.toString(), showMessage: false, message: {head: '', body: ''}})
    }else{
      this.setState({
        showMessage: true,
        message: {head: response.message.head, body: response.message.body}
      })
    }
  }

  sendCreateAccount = async (data: {email: string, password: string, repeatPassword: string}) =>{
    if(data.password === data.repeatPassword){
        const response = await this.sendData(data, 'create', 'POST')
        if(response.status){
          if(this.state.openPopup){
            this.setState({
              openPopup: false,
              unsortedUsers: [...this.state.unsortedUsers, data.email],
              sortedUsers: this.sortUsers([...this.state.unsortedUsers, data.email])
            })
          }
          this.setState({
            showMessage: true,
            message: {head: response.message.head, body:response.message.body}
          })
        }else if(!response.status){
          this.setState({
            showMessage: true,
            message: {head: response.message.head, body:response.message.body}
          })
        }
    }else{
      this.setState({
        showMessage: true,
        message: {head: 'Passwords dont match', body: 'Check that both password fields are the same.'}
      })
    }
  }

  deleteUser = async (name: string) =>{
    await this.sendData({name}, 'delete', 'DELETE')
    .then(response => {if(!response.status){
      alert('Could not delete user, please try again')
    }else{
      this.popup('delete')
      let newUsers = this.state.unsortedUsers
      let userIndex = newUsers.indexOf(name)
      newUsers.splice(userIndex, 1)
      this.setState({
        unsortedUsers: newUsers,
        sortedUsers: this.sortUsers(newUsers)
      })

    }})
  }

  async componentDidMount(){
    try{
    await fetch(`http://localhost:4000/allUsers`,
    {
      method: 'GET',
      mode: 'cors',
      headers:{'Content-Type' : 'application/json'},
    })
    .then(response => {
      if(response.ok){
          return response.json()
          .then(json =>{
          this.setState({
            unsortedUsers: json,
            sortedUsers: this.sortUsers(json)
          })
        })
      }else if(!response.ok){
        throw Error(response.statusText)
      }
    })
  }catch (error){
    console.log(error)
  }
  }


  sendResetPassword = async(data: {email: string}) =>{
    const response = await this.sendData(data, 'restore', 'POST')
    this.setState({
      showMessage: true,
      message: {head: response.message.head, body:response.message.body}
    })
  }

  hideMessage = () =>{
    this.setState({
      showMessage: false
    })
  }

  sortUsers = (data: Array<string>) =>{
    let oldUsers: Array<string> = data
    let newUsers: Array<Array<string>> = []
    for(let i =0; i<oldUsers.length; i++){
      newUsers.push(oldUsers.slice(0, 6))
    }
    console.log(newUsers)
    console.log(this.state.sortedUsers)
    console.log(oldUsers)
    return newUsers
  }

  popup = (type: string) =>{
    if(type === 'add'){
      this.setState({
        openPopup: !this.state.openPopup,
        addingUser: true
      })
    }else{
      this.setState({
        openPopup: !this.state.openPopup,
        addingUser: false
      })
    }
  }


  render(){
    console.log(this.state)
    return (
      <div className="App">
        {this.state.isLoggedIn ? <InterfacePage users={this.state.sortedUsers} deleteUser={this.deleteUser} addingUser={this.state.addingUser} popup={this.popup} openPopup={this.state.openPopup} showMessage={this.state.showMessage} sendCreateAccount={this.sendCreateAccount} message={this.state.message} name={this.state.user}/> : <LogInPage hideMessage={this.hideMessage} showMessage={this.state.showMessage} message={this.state.message} sendLogin={this.sendLogin} sendCreateAccount={this.sendCreateAccount} sendResetPassword={this.sendResetPassword}/>}
      </div>
    );
  }
}

export default Main
